declare module '*.scss' {
  const content: { [className: string]: string }
  export = content
}

declare module '*.txt' {
  const content: string
  export = content
}

declare module '*.bmp' {
  const content: string
  export = content
}
declare module '*.png' {
  const content: string
  export = content
}
declare module '*.jpg' {
  const content: string
  export = content
}
